const handleSubmit = async (event) => {
  event.preventDefault();

  const username = "your_username"; // Replace with actual username
  const password = "your_password"; // Replace with actual password (not recommended)

  const usuario = {
      email: formData.email,
      senha: formData.senha,
      nome: formData.nome,
  };

  const authHeader = "Basic " + btoa(username + ":" + password); // Not recommended

  try {
      const response = await fetch('https://unearthly-broomstick-5gq5g564r5gq2q6g-8080.app.github.dev/api/usuarios', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
              'Authorization': authHeader // Not recommended for production
          },
          body: JSON.stringify(usuario),
      });

      // ... rest of your logic
  } catch (error) {
      console.error('Erro na requisição:', error);
  }
};
